    private float MAX_ROTATION_X = 18f;
    private float MAX_ROTATION_Y = 18f;

    private Camera camera = new Camera();

    private float rotationX = 0f;
    private float rotationY = 0f;

    public void visual(float vx, float vy) {
        float newRotationX = rotationX + vx;
        if (Math.abs(newRotationX) > MAX_ROTATION_X) {
            newRotationX = Math.signum(newRotationX) * MAX_ROTATION_X;
            vx = newRotationX - rotationX;
        }
        rotationX = newRotationX;
        float newRotationY = rotationY + vy;
        if (Math.abs(newRotationY) > MAX_ROTATION_Y) {
            newRotationY = Math.signum(newRotationY) * MAX_ROTATION_Y;
            vy = newRotationY - rotationY;
        }
        rotationY = newRotationY;
/*
        setRotationX(newRotationY);
        setRotationY(-newRotationX);
*/
//        camera.save();
//        camera.rotateY(newRotationX);
//        camera.rotateX(-newRotationY);
//        camera.getMatrix(cameraMatrix);
//        camera.restore();

        camera.save();
        camera.rotateX(-vy);
        camera.rotateY(vx);
        camera.getMatrix(cameraMatrix);
        cameraMatrix.preTranslate(-widthSize / 2f, -heightSize / 2f);
        cameraMatrix.postTranslate(widthSize / 2f, heightSize / 2f);
        xMatrix.postConcat(cameraMatrix);
        camera.restore();
        invalidate();
    }

